// Format Jira issues for LLM
const items = $input.all();
const issues = items.map(item => item.json);

if (issues.length === 0) {
  return [{
    json: {
      taskCount: 0,
      taskList: "No active tasks assigned to you",
      rawTasks: [],
      hasActiveTasks: false,
      currentUser: "Subin"
    }
  }];
}

// Force current user to be Subin
const currentUser = "Subin";

const formatted = issues.map(issue => ({
  key: issue.key,
  summary: issue.fields?.summary || 'No summary',
  status: issue.fields?.status?.name || 'Unknown',
  priority: issue.fields?.priority?.name || 'None',
  assignee: issue.fields?.assignee?.displayName || 'Unassigned'
}));

// Categorize by priority
const highPriority = formatted.filter(t => t.priority === 'High' || t.priority === 'Highest');
const mediumPriority = formatted.filter(t => t.priority === 'Medium');
const lowPriority = formatted.filter(t => t.priority === 'Low' || t.priority === 'Lowest');
const noPriority = formatted.filter(t => t.priority === 'None' || !t.priority);

const taskList = formatted.map(t => 
  `• [${t.key}] ${t.summary} (${t.status} - ${t.priority})`
).join('\n');

// Create a simple prompt for the LLM
const userContent = `Hello Subin! You have ${formatted.length} active Jira tasks. High priority: ${highPriority.length}, Medium: ${mediumPriority.length}, Low: ${lowPriority.length + noPriority.length}. Create a brief motivational reminder (2-3 sentences) about what to focus on today.`;

return [{
  json: {
    currentUser: currentUser,
    taskCount: formatted.length,
    taskList: taskList,
    rawTasks: formatted,
    hasActiveTasks: true,
    userContent: userContent,
    highPriorityCount: highPriority.length,
    mediumPriorityCount: mediumPriority.length,
    lowPriorityCount: lowPriority.length + noPriority.length
  }
}];

