// Simple JQL analysis
const allTasks = $input.all().map(item => item.json);

if (allTasks.length === 0) {
  return {
    json: {
      message: `âŒ NO TASKS FOUND\n\nThe credential might be linked to the wrong account or you have no assigned tasks.\n\nTroubleshooting:\n1. Check if you have tasks in Jira web interface\n2. Verify credential is for correct account\n3. Try logging into Jira with the same credentials\n\nTime: ${new Date().toLocaleString('en-GB')}`
    }
  };
}

// Extract information
const assigneeInfo = allTasks[0]?.fields?.assignee;
const projects = [...new Set(allTasks.map(task => task.fields?.project?.key).filter(Boolean))];
const statuses = [...new Set(allTasks.map(task => task.fields?.status?.name).filter(Boolean))];

// Categorize tasks
const activeTasks = allTasks.filter(task => {
  const status = task.fields?.status?.name;
  return ['In Progress', 'To Do', 'Selected for Development', 'Open', 'New'].includes(status);
});

const inProgress = allTasks.filter(task => task.fields?.status?.name === 'In Progress');
const highPriority = allTasks.filter(task => ['High', 'Highest'].includes(task.fields?.priority?.name));

// Build comprehensive report
let message = `âœ… JQL ANALYSIS RESULTS\n\n`;
message += `ðŸ“Š FOUND ${allTasks.length} TOTAL TASKS\n\n`;

// Assignee info
if (assigneeInfo) {
  message += `ðŸ‘¤ YOU ARE IDENTIFIED AS:\n`;
  message += `   Name: ${assigneeInfo.displayName || 'Not available'}\n`;
  if (assigneeInfo.emailAddress) {
    message += `   Email: ${assigneeInfo.emailAddress}\n`;
  }
  message += `\n`;
}

// Project breakdown
if (projects.length > 0) {
  message += `ðŸ—ï¸ YOUR PROJECTS (${projects.length}):\n`;
  projects.forEach(project => {
    const projectTasks = allTasks.filter(t => t.fields?.project?.key === project).length;
    const activeProjectTasks = activeTasks.filter(t => t.fields?.project?.key === project).length;
    message += `   â€¢ ${project}: ${activeProjectTasks}/${projectTasks} active\n`;
  });
  message += `\n`;
}

// Task status breakdown
message += `ðŸ“ˆ TASK BREAKDOWN:\n`;
message += `   â€¢ Total Tasks: ${allTasks.length}\n`;
message += `   â€¢ Active Tasks: ${activeTasks.length}\n`;
message += `   â€¢ In Progress: ${inProgress.length}\n`;
message += `   â€¢ High Priority: ${highPriority.length}\n\n`;

// Current work
if (inProgress.length > 0) {
  message += `ðŸš€ CURRENTLY WORKING ON:\n`;
  inProgress.slice(0, 3).forEach(task => {
    message += `   â€¢ [${task.key}] ${task.fields?.summary || 'No summary'}\n`;
  });
  message += `\n`;
}

// Recommended JQL
message += `ðŸ’¡ RECOMMENDED JQL QUERIES:\n\n`;

if (projects.length === 1) {
  message += `âœ… Single Project:\n`;
  message += `assignee = currentUser() AND status IN ('In Progress', 'To Do', 'Selected for Development', 'Open')\n\n`;
} else if (projects.length > 1) {
  message += `âœ… Multi-Project (All):\n`;
  message += `assignee = currentUser() AND status IN ('In Progress', 'To Do', 'Selected for Development', 'Open')\n\n`;

  message += `âœ… Multi-Project (Specific):\n`;
  const projectList = projects.join(', ');
  message += `assignee = currentUser() AND project in (${projectList}) AND status IN ('In Progress', 'To Do')\n\n`;
}

// Alternative formats to try
if (assigneeInfo?.displayName) {
  message += `ðŸ”§ ALTERNATIVE FORMATS:\n`;
  message += `   â€¢ assignee = '${assigneeInfo.displayName}'\n`;
  if (assigneeInfo.emailAddress) {
    message += `   â€¢ assignee = '${assigneeInfo.emailAddress}'\n`;
  }
  message += `\n`;
}

message += `â° Analysis Time: ${new Date().toLocaleString('en-GB')}`;

return {
  json: {
    message: message,
    data: {
      totalTasks: allTasks.length,
      activeTasks: activeTasks.length,
      inProgress: inProgress.length,
      projects: projects,
      assignee: assigneeInfo,
      recommendedJQL: projects.length > 1 
        ? `assignee = currentUser() AND project in (${projects.join(', ')}) AND status IN ('In Progress', 'To Do', 'Selected for Development', 'Open')`
        : `assignee = currentUser() AND status IN ('In Progress', 'To Do', 'Selected for Development', 'Open')`
    }
  }
};
