// Process Telegram updates
const response = $input.first().json;

if (!response.ok || !response.result || response.result.length === 0) {
  return [];
}

const updates = response.result;
const processedUpdates = [];

for (const update of updates) {
  let command, text, userId, chatId;

  if (update.callback_query) {
    // Button press
    command = update.callback_query.data;
    userId = update.callback_query.from.id;
    chatId = update.callback_query.message.chat.id;
    text = update.callback_query.data;
  } else if (update.message) {
    // Direct message
    text = update.message.text || '';
    userId = update.message.from.id;
    chatId = update.message.chat.id;

    if (text.startsWith('/')) {
      const parts = text.split(' ');
      command = parts[0].toLowerCase();
    } else {
      continue; // Skip non-command messages
    }
  } else {
    continue; // Skip other update types
  }

  // Only respond to Subin (your user ID)
  const allowedUserId = 589824303;
  if (userId !== allowedUserId) {
    continue;
  }

  processedUpdates.push({
    json: {
      command: command,
      originalText: text,
      userId: userId,
      chatId: chatId,
      isCallbackQuery: !!update.callback_query,
      updateId: update.update_id
    }
  });
}

return processedUpdates;

// Create detailed today's plan for Subin
const items = $input.all();
const issues = items.map(item => item.json);
const chatId = $('Process Updates').item.json.chatId;

if (issues.length === 0) {
  return [{
    json: {
      chatId: chatId,
      message: "üéâ All Clear, Subin!\n\nNo active tasks today. Perfect time to:\n‚Ä¢ Plan ahead for upcoming work\n‚Ä¢ Help teammates\n‚Ä¢ Review and organize\n‚Ä¢ Take a strategic break\n\nYou're staying on top of things! üí™"
    }
  }];
}

const formatted = issues.map(issue => ({
  key: issue.key,
  summary: issue.fields?.summary || 'No summary',
  status: issue.fields?.status?.name || 'Unknown',
  priority: issue.fields?.priority?.name || 'None',
  project: issue.fields?.project?.key || 'UNKNOWN',
  estimate: issue.fields?.timeoriginalestimate || 0,
  spent: issue.fields?.timespent || 0,
  assignee: issue.fields?.assignee?.displayName || 'Unassigned',
  updated: new Date(issue.fields?.updated || Date.now())
}));

// Build detailed message
let message = `üìã Today's Plan for Subin\n\n`;

// Current work
const inProgress = formatted.filter(t => t.status === 'In Progress');
if (inProgress.length > 0) {
  message += `üöÄ CURRENTLY WORKING ON:\n`;
  inProgress.forEach(task => {
    message += `‚Ä¢ [${task.key}] ${task.summary}\n`;
  });
  message += `\n`;
} else {
  message += `‚ö° NO CURRENT WORK - Start with a high priority task!\n\n`;
}

// High priority tasks
const highPriority = formatted.filter(t => ['High', 'Highest'].includes(t.priority) && t.status !== 'In Progress');
if (highPriority.length > 0) {
  message += `üî¥ HIGH PRIORITY (Do Next):\n`;
  highPriority.slice(0, 3).forEach(task => {
    message += `‚Ä¢ [${task.key}] ${task.summary}\n`;
  });
  message += `\n`;
}

// Stats
message += `üìä QUICK STATS:\n`;
message += `‚Ä¢ Total Active: ${formatted.length}\n`;
message += `‚Ä¢ In Progress: ${inProgress.length}\n`;
message += `‚Ä¢ High Priority: ${highPriority.length}\n\n`;

message += `Generated: ${new Date().toLocaleString('en-GB')}`;

return [{
  json: {
    chatId: chatId,
    message: message
  }
}];

// Create status summary
const items = $input.all();
const issues = items.map(item => item.json);
const chatId = $('Process Updates').item.json.chatId;

if (issues.length === 0) {
  return [{
    json: {
      chatId: chatId,
      message: "üìä Status: All Clear! üéâ\n\nNo active tasks assigned to you."
    }
  }];
}

const formatted = issues.map(issue => ({
  key: issue.key,
  summary: issue.fields?.summary || 'No summary',
  status: issue.fields?.status?.name || 'Unknown',
  priority: issue.fields?.priority?.name || 'None'
}));

const inProgress = formatted.filter(t => t.status === 'In Progress');
const highPriority = formatted.filter(t => ['High', 'Highest'].includes(t.priority));

let message = `üìä Subin's Current Status\n\n`;

if (inProgress.length > 0) {
  message += `üöÄ CURRENTLY WORKING ON (${inProgress.length}):\n`;
  inProgress.forEach(task => {
    message += `‚Ä¢ [${task.key}] ${task.summary}\n`;
  });
} else {
  message += `‚ö° NO CURRENT WORK - Ready to start!\n`;
}

message += `\nüìà SUMMARY:\n`;
message += `‚Ä¢ Total Active: ${formatted.length}\n`;
message += `‚Ä¢ High Priority: ${highPriority.length}\n`;
message += `‚Ä¢ In Progress: ${inProgress.length}\n\n`;
message += `Updated: ${new Date().toLocaleString('en-GB')}`;

return [{
  json: {
    chatId: chatId,
    message: message
  }
}];

// Debug Jira responses
const currentUserData = $('Test currentUser()').all();
const emailQueryData = $('Test Email Query').all();
const akhilaQueryData = $('Test Akhila Query').all();
const chatId = $('Process Updates').item.json.chatId;

let message = `üîç JIRA DEBUG RESULTS\n\n`;
message += `1Ô∏è‚É£ currentUser() Query: ${currentUserData.length} tasks\n`;
if (currentUserData.length > 0) {
  const assignee = currentUserData[0].json.fields?.assignee;
  message += `   Assignee: ${assignee?.displayName || 'Unknown'} (${assignee?.emailAddress || 'No email'})\n`;
}

message += `\n2Ô∏è‚É£ subin@aramisimaging.com Query: ${emailQueryData.length} tasks\n`;
if (emailQueryData.length > 0) {
  const assignee = emailQueryData[0].json.fields?.assignee;
  message += `   Assignee: ${assignee?.displayName || 'Unknown'} (${assignee?.emailAddress || 'No email'})\n`;
}

message += `\n3Ô∏è‚É£ akhila@aramisimaging.com Query: ${akhilaQueryData.length} tasks\n`;
if (akhilaQueryData.length > 0) {
  const assignee = akhilaQueryData[0].json.fields?.assignee;
  message += `   Assignee: ${assignee?.displayName || 'Unknown'} (${assignee?.emailAddress || 'No email'})\n`;
}

message += `\nüéØ ANALYSIS:\n`;
if (currentUserData.length > 0 && emailQueryData.length === 0) {
  message += `‚ùå Credential linked to different email than subin@aramisimaging.com`;
} else if (emailQueryData.length > 0) {
  message += `‚úÖ subin@aramisimaging.com query works correctly`;
} else if (akhilaQueryData.length > 0) {
  message += `‚ö†Ô∏è Only Akhila's tasks found - credential is Akhila's account`;
} else {
  message += `‚ùì No tasks found - check Jira setup`;
}

message += `\n\nTime: ${new Date().toLocaleString('en-GB')}`;

return [{
  json: {
    chatId: chatId,
    message: message
  }
}];

